ctUri: 'http://localhost:9000', //YOUR-REDIRECT-URL\n});\n\nfunction logout() {\n  localStorage.removeItem('id_token');\n  localStorage.removeItem('access_token');\n  window.location.href = '/';\n}\n\nfunction showProfileInfo(profile) {\n  var btnLogin = document.getElementById('btn-login');\n  var btnLogout = document.getElementById('btn-logout');\n  var avatar = document.getElementById('avatar');\n  document.getElementById('nickname').textContent = profile.nickname;\n  btnLogin.style.display = 'none';\n  avatar.src = profile.picture;\n  avatar.style.display = 'block';\n  btnLogout.style.display = 'block';\n}\n\nfunction retrieveProfile() {\n  var idToken = localStorage.getItem('id_token');\n  if (idToken) {\n    try {\n      const profile = jwt_decode(idToken);\n      showProfileInfo(profile);\n    } catch (err) {\n      alert('There was an error getting the profile: ' + err.message);\n    }\n  }\n}\n\nauth0.parseHash(window.location.hash, (err, result) => {\n  if (err || !result) {\n    // Handle error\n    return;\n  }\n\n  // You can use the ID token to get user information in the frontend.\n  localStorage.setItem('id_token', result.idToken);\n  // You can use this token to interact with server-side APIs.\n  localStorage.setItem('access_token', result.accessToken);\n  retrieveProfile();\n});\n\nfunction afterLoad() {\n  // buttons\n  var btnLogin = document.getElementById('btn-login');\n  var btnLogout = document.getElementById('btn-logout');\n\n  btnLogin.addEventListener('click', function () {\n    auth0.authorize();\n  });\n\n  btnLogout.addEventListener('click', function () {\n    logout();\n  });\n\n  retrieveProfile();\n}\n\nwindow.addEventListener('load', afterLoad);\n```\n\n[\u3053\u306e\u30b3\u30fc\u30c9\u3092\u4f7f\u3063\u305f\u5b8c\u5168\u306a\u4f8b](https://github.com/auth0-blog/es2015-rundown-example) \u3092\u53d6\u5f97\u3057\u3066\u304f\u3060\u3055\u3044\u3002\n\n[\u30af\u30a4\u30c3\u30af \u30b9\u30bf\u30fc\u30c8 \u30c1\u30e5\u30fc\u30c8\u30ea\u30